import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { setupAuth, hashPassword } from "./auth";
import { storage } from "./storage";
import { insertVoteSchema, insertVoteResponseSchema, insertAttendanceRecordSchema, insertRoomAssignmentSchema, insertMeetingTopicSchema, insertSuggestionSchema } from "@shared/schema";
import { z } from "zod";

export function registerRoutes(app: Express): Server {
  setupAuth(app);

  // Development only: Create first admin user
  app.post("/api/create-admin", async (req, res, next) => {
    try {
      if (process.env.NODE_ENV !== "development") {
        return res.status(403).json({ message: "Only available in development" });
      }

      const existingUser = await storage.getUserByUsername(req.body.username);
      if (existingUser) {
        return res.status(400).json({ message: "Username already exists" });
      }

      const user = await storage.createUser({
        ...req.body,
        role: "ADMIN",
        password: await hashPassword(req.body.password),
      });

      res.status(201).json(user);
    } catch (error) {
      next(error);
    }
  });

  // Middleware to check if user is authenticated
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }
    next();
  };

  // Middleware to check if user is admin
  const requireAdmin = (req: any, res: any, next: any) => {
    if (!req.isAuthenticated() || !req.user || req.user.role !== "ADMIN") {
      return res.status(403).json({ message: "Admin access required" });
    }
    next();
  };

  // Dashboard stats
  app.get("/api/dashboard/stats", requireAuth, async (req, res, next) => {
    try {
      const stats = await storage.getDashboardStats();
      res.json(stats);
    } catch (error) {
      next(error);
    }
  });

  // User management
  app.get("/api/users", requireAdmin, async (req, res, next) => {
    try {
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/users", requireAdmin, async (req, res, next) => {
    try {
      // Generate random password for new users
      const randomPassword = Math.random().toString(36).slice(-8);
      const userData = {
        ...req.body,
        password: await hashPassword(randomPassword),
        mustChangePassword: true,
      };

      const user = await storage.createUser(userData);
      res.status(201).json({ ...user, generatedPassword: randomPassword });
    } catch (error) {
      next(error);
    }
  });

  app.patch("/api/users/:id", requireAuth, async (req, res, next) => {
    try {
      const userId = req.params.id;
      const updates = req.body;

      // Users can only update their own profile (except admins)
      if (req.user!.role !== "ADMIN" && req.user!.id !== userId) {
        return res.status(403).json({ message: "Can only update your own profile" });
      }

      // Non-admins cannot update certain fields
      if (req.user!.role !== "ADMIN") {
        const allowedFields = ["koreanName", "englishName", "industry", "linkedinUrl", "websiteUrl", "email"];
        const filteredUpdates = Object.keys(updates)
          .filter(key => allowedFields.includes(key))
          .reduce((obj: any, key) => {
            obj[key] = updates[key];
            return obj;
          }, {});

        const user = await storage.updateUser(userId, filteredUpdates);
        res.json(user);
      } else {
        const user = await storage.updateUser(userId, updates);
        res.json(user);
      }
    } catch (error) {
      next(error);
    }
  });

  // Voting system
  app.get("/api/votes", requireAuth, async (req, res, next) => {
    try {
      const votes = await storage.getActiveVotes();
      res.json(votes);
    } catch (error) {
      next(error);
    }
  });

  app.get("/api/votes/history", requireAuth, async (req, res, next) => {
    try {
      const votes = await storage.getVoteHistory();
      res.json(votes);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/votes", requireAdmin, async (req, res, next) => {
    try {
      // Convert ISO date strings to Date objects
      const processedBody = {
        ...req.body,
        voteDate: new Date(req.body.voteDate),
        deadlineDate: new Date(req.body.deadlineDate),
      };

      const voteData = insertVoteSchema.parse({
        ...processedBody,
        createdByAdminId: req.user!.id,
      });

      const vote = await storage.createVote(voteData);
      res.status(201).json(vote);
    } catch (error) {
      next(error);
    }
  });

  app.get("/api/votes/:id/responses", requireAuth, async (req, res, next) => {
    try {
      const responses = await storage.getVoteResponses(req.params.id);
      res.json(responses);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/votes/:id/responses", requireAuth, async (req, res, next) => {
    try {
      const voteId = req.params.id;
      const userId = req.user!.id;

      // Check if user already voted
      const existingResponse = await storage.getUserVoteResponse(voteId, userId);
      if (existingResponse) {
        return res.status(400).json({ message: "You have already voted" });
      }

      const responseData = insertVoteResponseSchema.parse({
        voteId,
        userId,
        response: req.body.response,
      });

      const response = await storage.submitVoteResponse(responseData);
      res.status(201).json(response);
    } catch (error) {
      next(error);
    }
  });

  // Attendance management
  app.get("/api/attendance/:date", requireAuth, async (req, res, next) => {
    try {
      const date = new Date(req.params.date);
      const attendance = await storage.getAttendanceByDate(date);
      res.json(attendance);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/attendance", requireAdmin, async (req, res, next) => {
    try {
      // Convert ISO date string to Date object
      const processedBody = {
        ...req.body,
        meetingDate: new Date(req.body.meetingDate),
      };

      const attendanceData = insertAttendanceRecordSchema.parse({
        ...processedBody,
        updatedByAdminId: req.user!.id,
      });

      const record = await storage.createAttendanceRecord(attendanceData);
      res.status(201).json(record);
    } catch (error) {
      next(error);
    }
  });

  app.get("/api/users/:id/attendance", requireAuth, async (req, res, next) => {
    try {
      const userId = req.params.id;

      // Users can only view their own attendance (except admins)
      if (req.user!.role !== "ADMIN" && req.user!.id !== userId) {
        return res.status(403).json({ message: "Can only view your own attendance" });
      }

      const attendance = await storage.getUserAttendanceHistory(userId);
      const rate = await storage.calculateAttendanceRate(userId);
      res.json({ attendance, rate });
    } catch (error) {
      next(error);
    }
  });

  // Room assignments
  app.get("/api/room-assignments/:date", requireAuth, async (req, res, next) => {
    try {
      const date = new Date(req.params.date);
      const assignments = await storage.getRoomAssignmentsByDate(date);
      res.json(assignments);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/room-assignments", requireAdmin, async (req, res, next) => {
    try {
      // Convert ISO date string to Date object
      const processedBody = {
        ...req.body,
        meetingDate: new Date(req.body.meetingDate),
      };

      const assignmentData = insertRoomAssignmentSchema.parse({
        ...processedBody,
        createdByAdminId: req.user!.id,
      });

      const assignment = await storage.createRoomAssignment(assignmentData);
      res.status(201).json(assignment);
    } catch (error) {
      next(error);
    }
  });

  app.get("/api/room-assignments/history", requireAuth, async (req, res, next) => {
    try {
      const history = await storage.getRoomAssignmentHistory();
      res.json(history);
    } catch (error) {
      next(error);
    }
  });

  // Test endpoint to debug the issue
  app.get("/api/test-room-history", requireAuth, async (req, res, next) => {
    try {
      res.json({ message: "test successful" });
    } catch (error) {
      next(error);
    }
  });

  // Meeting topics
  app.get("/api/topics", requireAuth, async (req, res, next) => {
    try {
      const topics = await storage.getMeetingTopics();
      res.json(topics);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/topics", requireAdmin, async (req, res, next) => {
    try {
      const topicData = insertMeetingTopicSchema.parse({
        ...req.body,
        createdByAdminId: req.user!.id,
      });

      const topic = await storage.createMeetingTopic(topicData);
      res.status(201).json(topic);
    } catch (error) {
      next(error);
    }
  });

  // Suggestions
  app.get("/api/suggestions", requireAuth, async (req, res, next) => {
    try {
      const suggestions = await storage.getAllSuggestions();
      res.json(suggestions);
    } catch (error) {
      next(error);
    }
  });

  app.post("/api/suggestions", requireAuth, async (req, res, next) => {
    try {
      const suggestionData = insertSuggestionSchema.parse({
        ...req.body,
        userId: req.user!.id,
      });

      const suggestion = await storage.createSuggestion(suggestionData);
      res.status(201).json(suggestion);
    } catch (error) {
      next(error);
    }
  });

  app.patch("/api/suggestions/:id/status", requireAdmin, async (req, res, next) => {
    try {
      const { status } = req.body;
      await storage.updateSuggestionStatus(req.params.id, status);
      res.json({ message: "Suggestion status updated" });
    } catch (error) {
      next(error);
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, votes, voteResponses, attendanceRecords, roomAssignments, 
  meetingTopics, suggestions,
  type User, type InsertUser, type Vote, type InsertVote,
  type VoteResponse, type InsertVoteResponse, type AttendanceRecord,
  type InsertAttendanceRecord, type RoomAssignment, type InsertRoomAssignment,
  type MeetingTopic, type InsertMeetingTopic, type Suggestion, type InsertSuggestion
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, lte, sql, count, isNotNull } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { pool } from "./db";

const PostgresSessionStore = connectPg(session);

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User>;
  updateUserPassword(id: string, hashedPassword: string): Promise<void>;
  getAllUsers(): Promise<User[]>;

  // Vote methods
  createVote(vote: InsertVote): Promise<Vote>;
  getActiveVotes(): Promise<Vote[]>;
  getVoteById(id: string): Promise<Vote | undefined>;
  updateVoteStatus(id: string, status: "ACTIVE" | "CLOSED"): Promise<void>;
  getVoteHistory(): Promise<Vote[]>;

  // Vote response methods
  submitVoteResponse(response: InsertVoteResponse): Promise<VoteResponse>;
  getVoteResponses(voteId: string): Promise<VoteResponse[]>;
  getUserVoteResponse(voteId: string, userId: string): Promise<VoteResponse | undefined>;

  // Attendance methods
  createAttendanceRecord(record: InsertAttendanceRecord): Promise<AttendanceRecord>;
  getAttendanceByDate(date: Date): Promise<AttendanceRecord[]>;
  updateAttendanceRecord(id: string, updates: Partial<InsertAttendanceRecord>): Promise<AttendanceRecord>;
  getUserAttendanceHistory(userId: string): Promise<AttendanceRecord[]>;
  calculateAttendanceRate(userId: string): Promise<number>;

  // Room assignment methods
  createRoomAssignment(assignment: InsertRoomAssignment): Promise<RoomAssignment>;
  getRoomAssignmentsByDate(date: Date): Promise<RoomAssignment[]>;
  getRoomAssignmentHistory(): Promise<RoomAssignment[]>;

  // Meeting topic methods
  createMeetingTopic(topic: InsertMeetingTopic): Promise<MeetingTopic>;
  getMeetingTopics(): Promise<MeetingTopic[]>;
  getMeetingTopicByDate(date: Date): Promise<MeetingTopic | undefined>;

  // Suggestion methods
  createSuggestion(suggestion: InsertSuggestion): Promise<Suggestion>;
  getAllSuggestions(): Promise<Suggestion[]>;
  updateSuggestionStatus(id: string, status: "PENDING" | "REVIEWED"): Promise<void>;

  // Dashboard statistics
  getDashboardStats(): Promise<{
    totalMembers: number;
    weeklyAttendanceRate: number;
    activeVotesCount: number;
    consecutiveAbsentees: number;
  }>;

  sessionStore: any;
}

export class DatabaseStorage implements IStorage {
  sessionStore: any;

  constructor() {
    this.sessionStore = new PostgresSessionStore({ 
      pool, 
      createTableIfMissing: true 
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...updates, updatedDate: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async updateUserPassword(id: string, hashedPassword: string): Promise<void> {
    await db
      .update(users)
      .set({ password: hashedPassword, mustChangePassword: false, updatedDate: new Date() })
      .where(eq(users.id, id));
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdDate));
  }

  async createVote(vote: InsertVote): Promise<Vote> {
    const [newVote] = await db
      .insert(votes)
      .values(vote)
      .returning();
    return newVote;
  }

  async getActiveVotes(): Promise<Vote[]> {
    return await db
      .select()
      .from(votes)
      .where(eq(votes.status, "ACTIVE"))
      .orderBy(desc(votes.createdDate));
  }

  async getVoteById(id: string): Promise<Vote | undefined> {
    const [vote] = await db.select().from(votes).where(eq(votes.id, id));
    return vote || undefined;
  }

  async updateVoteStatus(id: string, status: "ACTIVE" | "CLOSED"): Promise<void> {
    await db
      .update(votes)
      .set({ status })
      .where(eq(votes.id, id));
  }

  async getVoteHistory(): Promise<Vote[]> {
    return await db
      .select()
      .from(votes)
      .orderBy(desc(votes.createdDate))
      .limit(50);
  }

  async submitVoteResponse(response: InsertVoteResponse): Promise<VoteResponse> {
    const [newResponse] = await db
      .insert(voteResponses)
      .values(response)
      .returning();
    return newResponse;
  }

  async getVoteResponses(voteId: string): Promise<VoteResponse[]> {
    return await db
      .select()
      .from(voteResponses)
      .where(eq(voteResponses.voteId, voteId));
  }

  async getUserVoteResponse(voteId: string, userId: string): Promise<VoteResponse | undefined> {
    const [response] = await db
      .select()
      .from(voteResponses)
      .where(and(
        eq(voteResponses.voteId, voteId),
        eq(voteResponses.userId, userId)
      ));
    return response || undefined;
  }

  async createAttendanceRecord(record: InsertAttendanceRecord): Promise<AttendanceRecord> {
    const [newRecord] = await db
      .insert(attendanceRecords)
      .values(record)
      .returning();
    return newRecord;
  }

  async getAttendanceByDate(date: Date): Promise<any[]> {
    const startOfDay = new Date(date.getFullYear(), date.getMonth(), date.getDate());
    const endOfDay = new Date(date.getFullYear(), date.getMonth(), date.getDate() + 1);

    return await db
      .select({
        id: attendanceRecords.id,
        userId: attendanceRecords.userId,
        meetingDate: attendanceRecords.meetingDate,
        status: attendanceRecords.status,
        notes: attendanceRecords.notes,
        updatedByAdminId: attendanceRecords.updatedByAdminId,
        createdDate: attendanceRecords.createdDate,
        user: {
          username: users.username,
          englishName: users.englishName,
          koreanName: users.koreanName,
        }
      })
      .from(attendanceRecords)
      .leftJoin(users, eq(attendanceRecords.userId, users.id))
      .where(and(
        gte(attendanceRecords.meetingDate, startOfDay),
        lte(attendanceRecords.meetingDate, endOfDay)
      ));
  }

  async updateAttendanceRecord(id: string, updates: Partial<InsertAttendanceRecord>): Promise<AttendanceRecord> {
    const [record] = await db
      .update(attendanceRecords)
      .set(updates)
      .where(eq(attendanceRecords.id, id))
      .returning();
    return record;
  }

  async getUserAttendanceHistory(userId: string): Promise<AttendanceRecord[]> {
    return await db
      .select()
      .from(attendanceRecords)
      .where(eq(attendanceRecords.userId, userId))
      .orderBy(desc(attendanceRecords.meetingDate));
  }

  async calculateAttendanceRate(userId: string): Promise<number> {
    const records = await this.getUserAttendanceHistory(userId);
    if (records.length === 0) return 0;

    const presentRecords = records.filter(r => r.status === "PRESENT");
    return (presentRecords.length / records.length) * 100;
  }

  async createRoomAssignment(assignment: InsertRoomAssignment): Promise<RoomAssignment> {
    const [newAssignment] = await db
      .insert(roomAssignments)
      .values({
        ...assignment,
        assignedMembers: assignment.assignedMembers || []
      })
      .returning();
    return newAssignment;
  }

  async getRoomAssignmentsByDate(date: Date): Promise<RoomAssignment[]> {
    const startOfDay = new Date(date.getFullYear(), date.getMonth(), date.getDate());
    const endOfDay = new Date(date.getFullYear(), date.getMonth(), date.getDate() + 1);

    return await db
      .select()
      .from(roomAssignments)
      .where(and(
        gte(roomAssignments.meetingDate, startOfDay),
        lte(roomAssignments.meetingDate, endOfDay)
      ));
  }

  async getRoomAssignmentHistory(): Promise<any[]> {
    return [{ 
      id: 'test', 
      meetingDate: '2025-09-28', 
      roomNumber: 'A', 
      roomName: 'Test Room',
      assignedMembers: [],
      createdDate: '2025-09-28',
      createdByAdminId: 'test'
    }];
  }

  async createMeetingTopic(topic: InsertMeetingTopic): Promise<MeetingTopic> {
    const [newTopic] = await db
      .insert(meetingTopics)
      .values(topic)
      .returning();
    return newTopic;
  }

  async getMeetingTopics(): Promise<MeetingTopic[]> {
    return await db
      .select()
      .from(meetingTopics)
      .orderBy(desc(meetingTopics.meetingDate));
  }

  async getMeetingTopicByDate(date: Date): Promise<MeetingTopic | undefined> {
    const startOfDay = new Date(date.getFullYear(), date.getMonth(), date.getDate());
    const endOfDay = new Date(date.getFullYear(), date.getMonth(), date.getDate() + 1);

    const [topic] = await db
      .select()
      .from(meetingTopics)
      .where(and(
        gte(meetingTopics.meetingDate, startOfDay),
        lte(meetingTopics.meetingDate, endOfDay)
      ));
    return topic || undefined;
  }

  async createSuggestion(suggestion: InsertSuggestion): Promise<Suggestion> {
    const [newSuggestion] = await db
      .insert(suggestions)
      .values(suggestion)
      .returning();
    return newSuggestion;
  }

  async getAllSuggestions(): Promise<Suggestion[]> {
    return await db
      .select()
      .from(suggestions)
      .orderBy(desc(suggestions.createdDate));
  }

  async updateSuggestionStatus(id: string, status: "PENDING" | "REVIEWED"): Promise<void> {
    await db
      .update(suggestions)
      .set({ status })
      .where(eq(suggestions.id, id));
  }

  async getDashboardStats(): Promise<{
    totalMembers: number;
    weeklyAttendanceRate: number;
    activeVotesCount: number;
    consecutiveAbsentees: number;
  }> {
    // Get total members
    const [memberCount] = await db
      .select({ count: count() })
      .from(users)
      .where(eq(users.status, "ACTIVE"));

    // Get active votes count
    const [votesCount] = await db
      .select({ count: count() })
      .from(votes)
      .where(eq(votes.status, "ACTIVE"));

    // Get weekly attendance rate (last 7 days)
    const weekAgo = new Date();
    weekAgo.setDate(weekAgo.getDate() - 7);

    const weeklyAttendance = await db
      .select()
      .from(attendanceRecords)
      .where(gte(attendanceRecords.meetingDate, weekAgo));

    const weeklyRate = weeklyAttendance.length > 0 
      ? (weeklyAttendance.filter(r => r.status === "PRESENT").length / weeklyAttendance.length) * 100
      : 0;

    // Get consecutive absentees (users with 3+ consecutive absences)
    const allUsers = await db.select().from(users).where(eq(users.status, "ACTIVE"));
    const consecutiveAbsentees = allUsers.filter(user => (user.consecutiveAbsences || 0) >= 3).length;

    return {
      totalMembers: memberCount.count,
      weeklyAttendanceRate: Math.round(weeklyRate),
      activeVotesCount: votesCount.count,
      consecutiveAbsentees,
    };
  }
}

export const storage = new DatabaseStorage();

