client/src/pages/attendance-page.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { format, subDays, addDays, startOfWeek, endOfWeek } from "date-fns";
import { Sidebar } from "@/components/layout/sidebar";
import { Header } from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Calendar } from "@/components/ui/calendar";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  Calendar as CalendarIcon, 
  ChevronLeft, 
  ChevronRight, 
  Users, 
  UserCheck, 
  UserX, 
  Clock,
  Plus,
  Eye
} from "lucide-react";

type AttendanceStatus = "PRESENT" | "ABSENT" | "LATE" | "NO_SHOW";

interface AttendanceRecord {
  id: string;
  userId: string;
  meetingDate: string;
  status: AttendanceStatus;
  notes?: string;
  user: {
    username: string;
    englishName?: string;
    koreanName?: string;
  };
}

interface User {
  id: string;
  username: string;
  englishName?: string;
  koreanName?: string;
  attendanceRate: string;
}

const statusColors = {
  PRESENT: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
  LATE: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
  ABSENT: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
  NO_SHOW: "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200"
};

const statusLabels = {
  PRESENT: "Present",
  LATE: "Late",
  ABSENT: "Absent",
  NO_SHOW: "No Show"
};

export default function AttendancePage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [isMarkingAttendance, setIsMarkingAttendance] = useState(false);
  const [selectedUser, setSelectedUser] = useState<string>("");
  const [selectedStatus, setSelectedStatus] = useState<AttendanceStatus>("PRESENT");
  const [notes, setNotes] = useState("");

  const isAdmin = user?.role === "ADMIN";
  const dateString = format(selectedDate, "yyyy-MM-dd");

  // Fetch attendance for selected date
  const { data: attendanceRecords, isLoading: attendanceLoading } = useQuery({
    queryKey: ["/api/attendance", dateString],
    queryFn: () => fetch(`/api/attendance/${dateString}`).then(res => res.json()),
  });

  // Fetch all users for admin
  const { data: allUsers } = useQuery({
    queryKey: ["/api/users"],
    enabled: isAdmin,
  });

  // Mark attendance mutation
  const markAttendanceMutation = useMutation({
    mutationFn: (data: { userId: string; meetingDate: string; status: AttendanceStatus; notes?: string }) =>
      apiRequest("POST", `/api/attendance`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/attendance"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      setIsMarkingAttendance(false);
      setSelectedUser("");
      setNotes("");
      toast({
        title: "Success",
        description: "Attendance marked successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to mark attendance",
        variant: "destructive",
      });
    },
  });

  const handleMarkAttendance = () => {
    if (!selectedUser) return;

    markAttendanceMutation.mutate({
      userId: selectedUser,
      meetingDate: selectedDate.toISOString(),
      status: selectedStatus,
      notes: notes || undefined,
    });
  };

  const navigateDate = (direction: 'prev' | 'next') => {
    if (direction === 'prev') {
      setSelectedDate(subDays(selectedDate, 7));
    } else {
      setSelectedDate(addDays(selectedDate, 7));
    }
  };

  const getAttendanceStats = (records: AttendanceRecord[]) => {
    const total = records.length;
    const present = records.filter(r => r.status === "PRESENT").length;
    const late = records.filter(r => r.status === "LATE").length;
    const absent = records.filter(r => r.status === "ABSENT").length;
    const noShow = records.filter(r => r.status === "NO_SHOW").length;

    return { total, present, late, absent, noShow };
  };

  const stats = attendanceRecords ? getAttendanceStats(attendanceRecords) : null;

  return (
    <div className="min-h-screen flex bg-background">
      <Sidebar />

      <main className="flex-1 flex flex-col min-w-0">
        <Header title="Attendance" subtitle="Track and manage member attendance records." />

        <div className="flex-1 p-4 md:p-6 space-y-6 overflow-auto">
          <div className="flex items-center justify-between">
            {isAdmin && (
              <Dialog open={isMarkingAttendance} onOpenChange={setIsMarkingAttendance}>
                <DialogTrigger asChild>
                  <Button data-testid="button-mark-attendance">
                    <Plus className="h-4 w-4 mr-2" />
                    Mark Attendance
                  </Button>
                </DialogTrigger>
                <DialogContent className="sm:max-w-md">
                  <DialogHeader>
                    <DialogTitle>Mark Attendance</DialogTitle>
                    <DialogDescription>
                      Mark attendance for {format(selectedDate, "PPP")}
                    </DialogDescription>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="member">Member</Label>
                      <Select value={selectedUser} onValueChange={setSelectedUser}>
                        <SelectTrigger data-testid="select-member">
                          <SelectValue placeholder="Select a member" />
                        </SelectTrigger>
                        <SelectContent>
                          {allUsers && Array.isArray(allUsers) && (allUsers as User[]).map((user: User) => (
                            <SelectItem key={user.id} value={user.id}>
                              {user.englishName || user.username} ({user.username})
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="status">Status</Label>
                      <Select value={selectedStatus} onValueChange={(value: AttendanceStatus) => setSelectedStatus(value)}>
                        <SelectTrigger data-testid="select-status">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="PRESENT">Present</SelectItem>
                          <SelectItem value="LATE">Late</SelectItem>
                          <SelectItem value="ABSENT">Absent</SelectItem>
                          <SelectItem value="NO_SHOW">No Show</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="notes">Notes (optional)</Label>
                      <Textarea
                        id="notes"
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                        placeholder="Additional notes..."
                        data-testid="textarea-notes"
                      />
                    </div>
                  </div>
                  <DialogFooter>
                    <Button
                      variant="outline"
                      onClick={() => setIsMarkingAttendance(false)}
                      data-testid="button-cancel"
                    >
                      Cancel
                    </Button>
                    <Button
                      onClick={handleMarkAttendance}
                      disabled={!selectedUser || markAttendanceMutation.isPending}
                      data-testid="button-submit-attendance"
                    >
                      {markAttendanceMutation.isPending ? "Marking..." : "Mark Attendance"}
                    </Button>
                  </DialogFooter>
                </DialogContent>
              </Dialog>
            )}
          </div>

          <Tabs defaultValue="overview" className="space-y-6">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="overview" data-testid="tab-overview">Overview</TabsTrigger>
              <TabsTrigger value="calendar" data-testid="tab-calendar">Calendar View</TabsTrigger>
            </TabsList>

            <TabsContent value="overview" className="space-y-6">
              {/* Date Navigation */}
              <Card>
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => navigateDate('prev')}
                      data-testid="button-prev-week"
                    >
                      <ChevronLeft className="h-4 w-4" />
                    </Button>
                    <div className="text-center">
                      <h3 className="font-semibold text-lg">
                        Week of {format(startOfWeek(selectedDate), "MMM dd")} - {format(endOfWeek(selectedDate), "MMM dd, yyyy")}
                      </h3>
                      <p className="text-sm text-muted-foreground">
                        Selected: {format(selectedDate, "EEEE, MMMM dd, yyyy")}
                      </p>
                    </div>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => navigateDate('next')}
                      data-testid="button-next-week"
                    >
                      <ChevronRight className="h-4 w-4" />
                    </Button>
                  </div>
                </CardHeader>
              </Card>

              {/* Attendance Stats */}
              {stats && (
                <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
                  <Card>
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-2">
                        <Users className="h-5 w-5 text-muted-foreground" />
                        <div>
                          <p className="text-2xl font-bold">{stats.total}</p>
                          <p className="text-xs text-muted-foreground">Total</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                  <Card>
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-2">
                        <UserCheck className="h-5 w-5 text-green-600" />
                        <div>
                          <p className="text-2xl font-bold text-green-600">{stats.present}</p>
                          <p className="text-xs text-muted-foreground">Present</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                  <Card>
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-2">
                        <Clock className="h-5 w-5 text-yellow-600" />
                        <div>
                          <p className="text-2xl font-bold text-yellow-600">{stats.late}</p>
                          <p className="text-xs text-muted-foreground">Late</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                  <Card>
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-2">
                        <UserX className="h-5 w-5 text-red-600" />
                        <div>
                          <p className="text-2xl font-bold text-red-600">{stats.absent}</p>
                          <p className="text-xs text-muted-foreground">Absent</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                  <Card>
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-2">
                        <UserX className="h-5 w-5 text-gray-600" />
                        <div>
                          <p className="text-2xl font-bold text-gray-600">{stats.noShow}</p>
                          <p className="text-xs text-muted-foreground">No Show</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                </div>
              )}

              {/* Attendance Records */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <CalendarIcon className="h-5 w-5" />
                    <span>Attendance Records - {format(selectedDate, "PPP")}</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {attendanceLoading ? (
                    <div className="space-y-3">
                      {[...Array(5)].map((_, i) => (
                        <div key={i} className="flex items-center justify-between p-3 border rounded">
                          <div className="flex items-center space-x-3">
                            <Skeleton className="h-10 w-10 rounded" />
                            <div className="space-y-1">
                              <Skeleton className="h-4 w-32" />
                              <Skeleton className="h-3 w-24" />
                            </div>
                          </div>
                          <Skeleton className="h-6 w-16" />
                        </div>
                      ))}
                    </div>
                  ) : attendanceRecords && attendanceRecords.length > 0 ? (
                    <div className="space-y-2">
                      {attendanceRecords.map((record: AttendanceRecord) => (
                        <div
                          key={record.id}
                          className="flex items-center justify-between p-3 border rounded hover:bg-muted/50"
                          data-testid={`attendance-record-${record.id}`}
                        >
                          <div className="flex items-center space-x-3">
                            <div className="w-10 h-10 bg-primary/10 rounded flex items-center justify-center">
                              <span className="text-sm font-semibold text-primary">
                                {(record.user.englishName || record.user.username).charAt(0).toUpperCase()}
                              </span>
                            </div>
                            <div>
                              <p className="font-medium">
                                {record.user.englishName || record.user.username}
                              </p>
                              <p className="text-sm text-muted-foreground">
                                @{record.user.username}
                                {record.user.koreanName && ` â€¢ ${record.user.koreanName}`}
                              </p>
                              {record.notes && (
                                <p className="text-xs text-muted-foreground italic mt-1">
                                  {record.notes}
                                </p>
                              )}
                            </div>
                          </div>
                          <Badge className={statusColors[record.status]}>
                            {statusLabels[record.status]}
                          </Badge>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8">
                      <Eye className="h-12 w-12 text-muted-foreground mx-auto mb-3" />
                      <p className="text-muted-foreground">
                        No attendance records for {format(selectedDate, "PPP")}
                      </p>
                      {isAdmin && (
                        <p className="text-sm text-muted-foreground mt-1">
                          Click "Mark Attendance" to add records
                        </p>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="calendar" className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle>Calendar View</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="flex justify-center">
                    <Calendar
                      mode="single"
                      selected={selectedDate}
                      onSelect={(date) => date && setSelectedDate(date)}
                      className="rounded-md border"
                      data-testid="attendance-calendar"
                    />
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </main>
    </div>
  );
}

client/src/pages/auth-page.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertUserSchema } from "@shared/schema";
import { z } from "zod";
import { Redirect } from "wouter";
import { Users, MessageSquare, Trophy, Calendar } from "lucide-react";

const loginSchema = z.object({
  username: z.string().min(1, "Username is required"),
  password: z.string().min(1, "Password is required"),
});

const registerSchema = insertUserSchema.extend({
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type LoginFormData = z.infer<typeof loginSchema>;
type RegisterFormData = z.infer<typeof registerSchema>;

export default function AuthPage() {
  const { user, loginMutation, registerMutation } = useAuth();

  const loginForm = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
  });

  const registerForm = useForm<RegisterFormData>({
    resolver: zodResolver(registerSchema),
  });

  if (user) {
    return <Redirect to="/" />;
  }

  const onLogin = (data: LoginFormData) => {
    loginMutation.mutate(data);
  };

  const onRegister = (data: RegisterFormData) => {
    const { confirmPassword, ...registerData } = data;
    registerMutation.mutate(registerData);
  };

  return (
    <div className="min-h-screen flex">
      {/* Left side - Auth forms */}
      <div className="flex-1 flex items-center justify-center p-8 bg-background">
        <div className="w-full max-w-md space-y-6">
          <div className="text-center space-y-2">
            <div className="w-12 h-12 bg-primary rounded-full flex items-center justify-center mx-auto">
              <Users className="h-6 w-6 text-primary-foreground" />
            </div>
            <h1 className="text-2xl font-bold text-foreground">GCC English Meetup Club</h1>
            <p className="text-muted-foreground">Manage your English learning community</p>
          </div>

          <Tabs defaultValue="login" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="login" data-testid="tab-login">Login</TabsTrigger>
              <TabsTrigger value="register" data-testid="tab-register">Register</TabsTrigger>
            </TabsList>

            <TabsContent value="login">
              <Card>
                <CardHeader>
                  <CardTitle>Welcome back</CardTitle>
                  <CardDescription>
                    Enter your credentials to access your account
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <form onSubmit={loginForm.handleSubmit(onLogin)} className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="login-username">Username</Label>
                      <Input
                        id="login-username"
                        data-testid="input-login-username"
                        {...loginForm.register("username")}
                        disabled={loginMutation.isPending}
                      />
                      {loginForm.formState.errors.username && (
                        <p className="text-sm text-destructive">
                          {loginForm.formState.errors.username.message}
                        </p>
                      )}
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="login-password">Password</Label>
                      <Input
                        id="login-password"
                        type="password"
                        data-testid="input-login-password"
                        {...loginForm.register("password")}
                        disabled={loginMutation.isPending}
                      />
                      {loginForm.formState.errors.password && (
                        <p className="text-sm text-destructive">
                          {loginForm.formState.errors.password.message}
                        </p>
                      )}
                    </div>

                    <Button
                      type="submit"
                      className="w-full"
                      disabled={loginMutation.isPending}
                      data-testid="button-login"
                    >
                      {loginMutation.isPending ? "Signing in..." : "Sign in"}
                    </Button>
                  </form>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="register">
              <Card>
                <CardHeader>
                  <CardTitle>Create your account</CardTitle>
                  <CardDescription>
                    Join the GCC English Meetup Club community
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <form onSubmit={registerForm.handleSubmit(onRegister)} className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="register-username">Username</Label>
                        <Input
                          id="register-username"
                          data-testid="input-register-username"
                          {...registerForm.register("username")}
                          disabled={registerMutation.isPending}
                        />
                        {registerForm.formState.errors.username && (
                          <p className="text-sm text-destructive">
                            {registerForm.formState.errors.username.message}
                          </p>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="register-email">Email</Label>
                        <Input
                          id="register-email"
                          type="email"
                          data-testid="input-register-email"
                          {...registerForm.register("email")}
                          disabled={registerMutation.isPending}
                        />
                        {registerForm.formState.errors.email && (
                          <p className="text-sm text-destructive">
                            {registerForm.formState.errors.email.message}
                          </p>
                        )}
                      </div>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="register-korean-name">Korean Name</Label>
                        <Input
                          id="register-korean-name"
                          data-testid="input-register-korean-name"
                          {...registerForm.register("koreanName")}
                          disabled={registerMutation.isPending}
                        />
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="register-english-name">English Name</Label>
                        <Input
                          id="register-english-name"
                          data-testid="input-register-english-name"
                          {...registerForm.register("englishName")}
                          disabled={registerMutation.isPending}
                        />
                      </div>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="register-password">Password</Label>
                      <Input
                        id="register-password"
                        type="password"
                        data-testid="input-register-password"
                        {...registerForm.register("password")}
                        disabled={registerMutation.isPending}
                      />
                      {registerForm.formState.errors.password && (
                        <p className="text-sm text-destructive">
                          {registerForm.formState.errors.password.message}
                        </p>
                      )}
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="register-confirm-password">Confirm Password</Label>
                      <Input
                        id="register-confirm-password"
                        type="password"
                        data-testid="input-register-confirm-password"
                        {...registerForm.register("confirmPassword")}
                        disabled={registerMutation.isPending}
                      />
                      {registerForm.formState.errors.confirmPassword && (
                        <p className="text-sm text-destructive">
                          {registerForm.formState.errors.confirmPassword.message}
                        </p>
                      )}
                    </div>

                    <Button
                      type="submit"
                      className="w-full"
                      disabled={registerMutation.isPending}
                      data-testid="button-register"
                    >
                      {registerMutation.isPending ? "Creating account..." : "Create account"}
                    </Button>
                  </form>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </div>

      {/* Right side - Hero section */}
      <div className="hidden lg:flex flex-1 bg-primary">
        <div className="flex flex-col justify-center p-12 text-primary-foreground">
          <div className="space-y-8">
            <div>
              <h2 className="text-4xl font-bold mb-4">
                Welcome to GCC English Meetup Club
              </h2>
              <p className="text-xl text-primary-foreground/90">
                Join our vibrant community of English learners and practice your skills 
                in a supportive environment.
              </p>
            </div>

            <div className="space-y-6">
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 bg-primary-foreground/20 rounded-lg flex items-center justify-center">
                  <MessageSquare className="h-6 w-6" />
                </div>
                <div>
                  <h3 className="font-semibold">Interactive Discussions</h3>
                  <p className="text-primary-foreground/80">
                    Participate in engaging conversations and improve your speaking skills
                  </p>
                </div>
              </div>

              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 bg-primary-foreground/20 rounded-lg flex items-center justify-center">
                  <Trophy className="h-6 w-6" />
                </div>
                <div>
                  <h3 className="font-semibold">Achievement System</h3>
                  <p className="text-primary-foreground/80">
                    Progress through Honor levels as you improve your English proficiency
                  </p>
                </div>
              </div>

              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 bg-primary-foreground/20 rounded-lg flex items-center justify-center">
                  <Calendar className="h-6 w-6" />
                </div>
                <div>
                  <h3 className="font-semibold">Regular Meetups</h3>
                  <p className="text-primary-foreground/80">
                    Join weekly sessions with structured topics and interactive activities
                  </p>
                </div>
              </div>
            </div>

            <div className="pt-8 border-t border-primary-foreground/20">
              <p className="text-sm text-primary-foreground/70">
                Connect with like-minded learners and build lasting friendships while 
                improving your English communication skills.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/dashboard-page.tsx.content:
import { Sidebar } from "@/components/layout/sidebar";
import { Header } from "@/components/layout/header";
import { StatsCard } from "@/components/dashboard/stats-card";
import { QuickActions } from "@/components/dashboard/quick-actions";
import { CurrentVote } from "@/components/dashboard/current-vote";
import { RecentActivity } from "@/components/dashboard/recent-activity";
import { MemberOverview } from "@/components/dashboard/member-overview";
import { UpcomingEvents } from "@/components/dashboard/upcoming-events";
import { Announcements } from "@/components/dashboard/announcements";
import { useQuery } from "@tanstack/react-query";
import { Users, CalendarCheck, Vote, AlertTriangle } from "lucide-react";

export default function DashboardPage() {
  const { data: stats, isLoading: isStatsLoading } = useQuery({
    queryKey: ["/api/dashboard/stats"],
  });

  const { data: votes, isLoading: isVotesLoading } = useQuery({
    queryKey: ["/api/votes"],
  });

  const activeVote = votes && votes.length > 0 ? votes[0] : null;

  return (
    <div className="min-h-screen flex bg-background">
      <Sidebar />

      <main className="flex-1 flex flex-col min-w-0">
        <Header title="Dashboard" subtitle="Welcome back! Here's what's happening with your club." />

        <div className="flex-1 p-4 md:p-6 space-y-6 overflow-auto">
          {/* Statistics Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 md:gap-6">
            <StatsCard
              title="Total Members"
              value={stats?.totalMembers ?? 0}
              icon={Users}
              change={"+2 this month"}
              changeType="positive"
              isLoading={isStatsLoading}
              data-testid="stat-total-members"
            />
            <StatsCard
              title="This Week's Attendance"
              value={`${stats?.weeklyAttendanceRate ?? 0}%`}
              icon={CalendarCheck}
              change="+5% from last week"
              changeType="positive"
              isLoading={isStatsLoading}
              data-testid="stat-weekly-attendance"
            />
            <StatsCard
              title="Active Votes"
              value={stats?.activeVotesCount ?? 0}
              icon={Vote}
              change="Deadline: 2 hours"
              changeType="neutral"
              isLoading={isStatsLoading}
              data-testid="stat-active-votes"
            />
            <StatsCard
              title="Consecutive Absences"
              value={stats?.consecutiveAbsentees ?? 0}
              icon={AlertTriangle}
              change="Members need attention"
              changeType="negative"
              isLoading={isStatsLoading}
              data-testid="stat-consecutive-absences"
            />
          </div>

          {/* Quick Actions & Current Vote */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div className="lg:col-span-1">
              <QuickActions />
            </div>
            <div className="lg:col-span-2">
              <CurrentVote vote={activeVote} isLoading={isVotesLoading} />
            </div>
          </div>

          {/* Recent Activity & Member Overview */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <RecentActivity />
            <MemberOverview />
          </div>

          {/* Upcoming Events & Announcements */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <UpcomingEvents />
            <Announcements />
          </div>
        </div>
      </main>
    </div>
  );
}

client/src/pages/members-page.tsx.content:
import { Sidebar } from "@/components/layout/sidebar";
import { Header } from "@/components/layout/header";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { Search, UserPlus, Mail, Phone, ExternalLink } from "lucide-react";
import { useState } from "react";

export default function MembersPage() {
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [levelFilter, setLevelFilter] = useState("all");

  const { data: members, isLoading } = useQuery({
    queryKey: ["/api/users"],
  });

  const filteredMembers = (members || []).filter((member: any) => {
    const matchesSearch = 
      member.englishName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      member.koreanName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      member.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      member.email?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === "all" || member.status === statusFilter;
    const matchesLevel = levelFilter === "all" || member.membershipLevel === levelFilter;

    return matchesSearch && matchesStatus && matchesLevel;
  }) || [];

  const getMembershipLevelColor = (level: string) => {
    switch (level) {
      case "HONOR_IV": return "bg-yellow-500";
      case "HONOR_III": return "bg-orange-500";
      case "HONOR_II": return "bg-blue-500";
      case "HONOR_I": return "bg-green-500";
      default: return "bg-gray-500";
    }
  };

  const getMembershipLevelText = (level: string) => {
    switch (level) {
      case "HONOR_IV": return "Honor IV";
      case "HONOR_III": return "Honor III";
      case "HONOR_II": return "Honor II";
      case "HONOR_I": return "Honor I";
      default: return level;
    }
  };

  return (
    <div className="min-h-screen flex bg-background">
      <Sidebar />

      <main className="flex-1 flex flex-col min-w-0">
        <Header title="Members" subtitle="Manage club members and their information." />

        <div className="flex-1 p-4 md:p-6 space-y-6 overflow-auto">
          {/* Filters and Actions */}
          <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
            <div className="flex flex-col sm:flex-row gap-4 flex-1">
              <div className="relative flex-1 max-w-sm">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search members..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-members"
                />
              </div>

              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-[180px]" data-testid="select-status-filter">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="ACTIVE">Active</SelectItem>
                  <SelectItem value="INACTIVE">Inactive</SelectItem>
                </SelectContent>
              </Select>

              <Select value={levelFilter} onValueChange={setLevelFilter}>
                <SelectTrigger className="w-[180px]" data-testid="select-level-filter">
                  <SelectValue placeholder="Filter by level" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Levels</SelectItem>
                  <SelectItem value="HONOR_IV">Honor IV</SelectItem>
                  <SelectItem value="HONOR_III">Honor III</SelectItem>
                  <SelectItem value="HONOR_II">Honor II</SelectItem>
                  <SelectItem value="HONOR_I">Honor I</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {user?.role === "ADMIN" && (
              <Button data-testid="button-add-member">
                <UserPlus className="h-4 w-4 mr-2" />
                Add Member
              </Button>
            )}
          </div>

          {/* Members Table */}
          <Card>
            <CardHeader>
              <CardTitle>Club Members</CardTitle>
              <CardDescription>
                {filteredMembers.length} of {(members || []).length} members
              </CardDescription>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="space-y-4">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <div key={i} className="animate-pulse space-y-2">
                      <div className="h-4 bg-muted rounded w-full"></div>
                      <div className="h-4 bg-muted rounded w-3/4"></div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Name</TableHead>
                        <TableHead>Username</TableHead>
                        <TableHead>Level</TableHead>
                        <TableHead>Industry</TableHead>
                        <TableHead>Attendance Rate</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>Contact</TableHead>
                        <TableHead>Actions</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {filteredMembers.map((member: any) => (
                        <TableRow key={member.id} data-testid={`member-row-${member.id}`}>
                          <TableCell>
                            <div>
                              <div className="font-medium">{member.englishName || "N/A"}</div>
                              {member.koreanName && (
                                <div className="text-sm text-muted-foreground">{member.koreanName}</div>
                              )}
                            </div>
                          </TableCell>
                          <TableCell>{member.username}</TableCell>
                          <TableCell>
                            <div className="flex items-center space-x-2">
                              <div className={`w-3 h-3 rounded-full ${getMembershipLevelColor(member.membershipLevel)}`}></div>
                              <span className="text-sm font-medium">
                                {getMembershipLevelText(member.membershipLevel)}
                              </span>
                            </div>
                          </TableCell>
                          <TableCell>
                            {member.industry ? (
                              <Badge variant="outline">{member.industry}</Badge>
                            ) : (
                              <span className="text-muted-foreground">N/A</span>
                            )}
                          </TableCell>
                          <TableCell>
                            <div className="text-sm">
                              {member.attendanceRate ? `${member.attendanceRate}%` : "0%"}
                            </div>
                          </TableCell>
                          <TableCell>
                            <Badge variant={member.status === "ACTIVE" ? "default" : "secondary"}>
                              {member.status}
                            </Badge>
                          </TableCell>
                          <TableCell>
                            <div className="flex items-center space-x-2">
                              {member.email && (
                                <Button variant="ghost" size="sm" data-testid={`button-email-${member.id}`}>
                                  <Mail className="h-4 w-4" />
                                </Button>
                              )}
                              {user?.role === "ADMIN" && member.phoneNumber && (
                                <Button variant="ghost" size="sm" data-testid={`button-phone-${member.id}`}>
                                  <Phone className="h-4 w-4" />
                                </Button>
                              )}
                              {member.linkedinUrl && (
                                <Button variant="ghost" size="sm" data-testid={`button-linkedin-${member.id}`}>
                                  <ExternalLink className="h-4 w-4" />
                                </Button>
                              )}
                            </div>
                          </TableCell>
                          <TableCell>
                            <Button variant="outline" size="sm" data-testid={`button-view-profile-${member.id}`}>
                              View Profile
                            </Button>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </div>
              )}

              {!isLoading && filteredMembers.length === 0 && (
                <div className="text-center py-8">
                  <p className="text-muted-foreground">No members found matching your criteria.</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

